refsynopsisdiv.synopsis:
CREATE [ OR REPLACE ] RULE isim AS ON olay
    TO tablo [ WHERE koşul ]
    DO [ ALSO | INSTEAD ] { NOTHING | komut | ( komut ; komut ... ) }

para.programlisting:
CREATE RULE "_RETURN" AS
    ON SELECT TO t1
    DO INSTEAD
        SELECT * FROM t2;

CREATE RULE "_RETURN" AS
    ON SELECT TO t2
    DO INSTEAD
        SELECT * FROM t1;

SELECT * FROM t1;
